  static async getUsers(params: {
    page?: number
    limit?: number
    search?: string
    role?: string
  } = {}) {
    console.log('üë• Fetching users...');
    
    const mockUsers = [
      {
        id: 'user-1',
        primerica_id: 'ADMIN001',
        first_name: 'Admin',
        last_name: 'Principal',
        email: 'admin@certifi.quebec',
        initial_role: 'LES_DEUX',
        current_xp: 5000,
        current_level: 8,
        gamified_role: 'Ma√Ætre Administrateur',
        is_admin: true,
        is_supreme_admin: true,
        is_active: true,
        created_at: new Date().toISOString()
      }
    ];

    // Appliquer les filtres
    let users = mockUsers;
    if (params.search) {
      const search = params.search.toLowerCase();
      users = users.filter(user => 
        user.first_name.toLowerCase().includes(search) ||
        user.last_name.toLowerCase().includes(search) ||
        user.primerica_id.toLowerCase().includes(search) ||
        user.email.toLowerCase().includes(search)
      );
    }

    if (params.role) {
      users = users.filter(user => user.initial_role === params.role);
    }

    // Pagination
    const page = params.page || 1;
    const limit = params.limit || 20;
    const offset = (page - 1) * limit;
    const paginatedUsers = users.slice(offset, offset + limit);

    return {
      users: paginatedUsers,
      pagination: {
        page,
        limit,
        total: users.length,
        totalPages: Math.ceil(users.length / limit)
      }
    };
  }

